package com.janbask.arithmeticOperators;

public class Increment_operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=2;
		
		a++;//3
		
		a++;//4
		
		a++;//5
		
		a++;//6
		
		a=a+4;//10
		
		a++;//11
		a=a+2;//13
		//a++  or a= a+1
		
		
		System.out.println(a);//  13 

	}

}
